const allItems = $input.all();
const prospectIds = allItems.map(item => 
  item.json.matched_prospects.map(prospect => prospect.prospect_id)
).flat();

return [{
  json: {
    prospect_ids: prospectIds
  }
}];

const items = [];

for (const item of $input.all()) {
  if (item.json.enriched_data && item.json.enriched_data.length > 0) {
    items.push({
      json: {
        prospect_id: item.json.enriched_data[0].prospect_id,
        ...item.json.enriched_data[0].data
      }
    });
  }
}

return items;
