// Get the input data
let data = $input.all()[0].json;

// Handle different possible structures
let report;
if (Array.isArray(data)) {
  report = data[0];
} else if (data.factMap) {
  report = data;
} else {
  throw new Error('Unexpected data structure');
}

// Check if we have the factMap
if (!report || !report.factMap) {
  throw new Error('No factMap found in report data');
}

// Extract rows from the report
const rows = report.factMap['T!T'].rows;

// Map each row to extract the fields you need
const extractedData = rows.map(row => {
  const cells = row.dataCells;

  return {
    firstName: cells[2].label,
    lastName: cells[3].label,
    email: cells[4].label,
    company: cells[5].label,
    leadSource: cells[1].label,
    status: cells[6].label,
    createdDate: cells[0].label,
    recordId: cells[2].recordId,
    TenantName: cells[9]?.label || ''
  };
});

return extractedData.map(item => ({ json: item }));

// Extract tenant names formatted for SQL IN clause
const leads = $input.all();
const tenantNames = leads.map(item => `'${item.json.TenantName}'`);
const sqlFormattedList = tenantNames.join(', ');

return [{ json: { tenantNames: sqlFormattedList } }];

// Extract business names with quotes as separate items
const leads = $input.all();
const businessNames = leads.map(item => `"${item.json.company}"`);

return businessNames.map(name => ({ json: { businessName: name } }));

// Get all merged data from current node
const mergedInput = $input.all();
const leadData = $('Extract Records').all(); // Your lead data node

// Separate API usage data from enrichment data
const apiUsageData = [];
const enrichmentData = [];

mergedInput.forEach(item => {
  if (item.json.TenantNames) {
    // This is API usage data
    apiUsageData.push(item);
  } else if (item.json.response_context) {
    // This is enrichment data
    enrichmentData.push(item);
  }
});

// Create lookup map for API usage by tenant name
const apiUsageMap = {};
apiUsageData.forEach(item => {
  apiUsageMap[item.json.TenantNames] = {
    endpoints: item.json.endpoints,
    endpointsNum: item.json.endpointsNum
  };
});

// Create lookup map for enrichment data by company name
const enrichmentMap = {};
enrichmentData.forEach(item => {
  const companyData = item.json.data?.[0]?.data;
  if (companyData) {
    enrichmentMap[companyData.name] = {
      business_id: companyData.business_id,
      business_description: companyData.business_description,
      website: companyData.website,
      country: companyData.country_name,
      region: companyData.region_name,
      city: companyData.city_name,
      street: companyData.street,
      zip_code: companyData.zip_code,
      naics: companyData.naics,
      naics_description: companyData.naics_description,
      sic_code: companyData.sic_code,
      sic_code_description: companyData.sic_code_description,
      employees: companyData.number_of_employees_range,
      revenue: companyData.yearly_revenue_range,
      industry: companyData.linkedin_industry_category,
      linkedin: companyData.linkedin_profile,
      logo: companyData.business_logo
    };
  }
});

// Merge all data with lead data
const mergedData = leadData.map(lead => {
  const tenantName = lead.json.TenantName;
  const companyName = lead.json.company;

  const apiUsage = apiUsageMap[tenantName] || { 
    endpoints: "[]", 
    endpointsNum: "0" 
  };
  const enrichment = enrichmentMap[companyName] || {};

  return {
    // Lead info
    firstName: lead.json.firstName,
    lastName: lead.json.lastName,
    email: lead.json.email,
    company: companyName,
    leadSource: lead.json.leadSource,
    status: lead.json.status,
    createdDate: lead.json.createdDate,
    recordId: lead.json.recordId,
    tenantName: tenantName,

    // API usage info
    endpoints: apiUsage.endpoints,
    endpointsNum: apiUsage.endpointsNum,

    // Company enrichment data
    ...enrichment
  };
});

return mergedData.map(item => ({ json: item }));

// Format lead qualification analysis for Salesforce task description
const rawData = $input.item.json;
const data = Array.isArray(rawData) ? rawData[0].output : (rawData.output || rawData);

// Build formatted description
let description = '';

// Lead Score Badge
const scoreBadge = {
  'High Priority': 'ðŸ”´ HIGH PRIORITY',
  'Medium Priority': 'ðŸŸ¡ MEDIUM PRIORITY',
  'Low Priority': 'ðŸŸ¢ LOW PRIORITY',
  'Nurture': 'âšª NURTURE'
};

description += `${scoreBadge[data.lead_score] || data.lead_score}\n`;
description += `${'='.repeat(50)}\n\n`;

// Quick Summary
description += `ðŸ“‹ QUICK SUMMARY\n`;
description += `${data.quick_summary}\n\n`;

// API Usage Analysis
description += `${'â”€'.repeat(50)}\n`;
description += `ðŸ”Œ API USAGE ANALYSIS\n`;
description += `${'â”€'.repeat(50)}\n`;

if (data.api_usage_analysis.endpoints_used && data.api_usage_analysis.endpoints_used.length > 0) {
  description += `Endpoints Used:\n`;
  data.api_usage_analysis.endpoints_used.forEach(endpoint => {
    description += `  â€¢ ${endpoint}\n`;
  });
} else {
  description += `Endpoints Used: None\n`;
}

description += `\nPotential Use Case: ${data.api_usage_analysis.potential_use_case}\n`;

if (data.api_usage_analysis.usage_insights && data.api_usage_analysis.usage_insights.length > 0) {
  description += `\nUsage Insights:\n`;
  data.api_usage_analysis.usage_insights.forEach(insight => {
    description += `  â€¢ ${insight}\n`;
  });
}
description += `\n`;

// Company Profile
description += `${'â”€'.repeat(50)}\n`;
description += `ðŸ¢ COMPANY PROFILE\n`;
description += `${'â”€'.repeat(50)}\n`;
description += `${data.company_profile.company_overview}\n\n`;
description += `Fit Assessment: ${data.company_profile.fit_assessment}\n\n`;
description += `Potential Value: ${data.company_profile.potential_value}\n\n`;

// Quality Signals
description += `${'â”€'.repeat(50)}\n`;
description += `âœ… QUALITY SIGNALS\n`;
description += `${'â”€'.repeat(50)}\n`;

if (data.quality_signals.positive_signals && data.quality_signals.positive_signals.length > 0) {
  description += `Positive Signals:\n`;
  data.quality_signals.positive_signals.forEach(signal => {
    description += `  âœ“ ${signal}\n`;
  });
}

if (data.quality_signals.concerns && data.quality_signals.concerns.length > 0) {
  description += `\nConcerns:\n`;
  data.quality_signals.concerns.forEach(concern => {
    description += `  âš  ${concern}\n`;
  });
}
description += `\n`;

// Recommended Actions
description += `${'â”€'.repeat(50)}\n`;
description += `ðŸŽ¯ RECOMMENDED ACTIONS\n`;
description += `${'â”€'.repeat(50)}\n`;
description += `Next Step: ${data.recommended_actions.immediate_next_step}\n\n`;
description += `Timing: ${data.recommended_actions.outreach_timing}\n\n`;
description += `Approach: ${data.recommended_actions.suggested_approach}\n\n`;

// Talking Points
description += `${'â”€'.repeat(50)}\n`;
description += `ðŸ’¬ PERSONALIZED TALKING POINTS\n`;
description += `${'â”€'.repeat(50)}\n`;
if (data.talking_points && data.talking_points.length > 0) {
  data.talking_points.forEach((point, index) => {
    description += `${index + 1}. ${point}\n`;
  });
}

// Map lead score to Salesforce priority
const priorityMap = {
  'High Priority': 'High',
  'Medium Priority': 'Normal',
  'Low Priority': 'Low',
  'Nurture': 'Low'
};

return [{
  json: {
    Description: description,
    Priority: priorityMap[data.lead_score] || 'Normal',
    Subject: `Lead Qualification: ${data.lead_score}`,
    Status: 'Not Started',
    lead_score: data.lead_score
  }
}];

parameters.text:
=Analyze the following inbound leads and their API usage:

LEAD INFORMATION:
- Name: {{ $('Loop Over Items5').item.json.firstName }} {{ $('Loop Over Items5').item.json.lastName }}
- Email: {{ $('Loop Over Items5').item.json.email }}
- Company: {{ $('Loop Over Items5').item.json.company }}
- Lead Source: {{ $('Loop Over Items5').item.json.leadSource }}
- Status: {{ $('Loop Over Items5').item.json.status }}
- Created: {{ $('Loop Over Items5').item.json.createdDate }}


API USAGE DATA:
- Endpoints: {{ $('Loop Over Items5').item.json.endpoints }}

FIRMOGRAPHICS:
- Business Description: {{ $('Loop Over Items5').item.json.business_description }}
- Website: {{ $('Loop Over Items5').item.json.website }}
- City: {{ $('Loop Over Items5').item.json.city }}
- Employees: {{ $('Loop Over Items5').item.json.employees }}
- Revenue: {{ $('Loop Over Items5').item.json.revenue }}
- Industry: {{ $('Loop Over Items5').item.json.industry }}

For each lead, provide:
1. **Lead Quality Score** (High/Medium/Low) - based on company legitimacy, email domain, and usage
2. **API Usage Summary** - which endpoints they're testing and what that indicates about their use case
3. **Recommended Action** - Should we reach out? What's the priority?
4. **Personalized Talking Points** - 2-3 specific points mentioning their actual API usage to use in outreach
5. **Red Flags** (if any) - students, fake emails, zero usage, etc.

Focus on leads that show genuine enterprise interest with meaningful API exploration.

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "lead_score": {
      "type": "string",
      "enum": ["High Priority", "Medium Priority", "Low Priority", "Nurture"],
      "description": "Overall lead quality assessment based on usage, company profile, and signals"
    },
    "quick_summary": {
      "type": "string",
      "description": "2-3 sentence executive summary of this lead's potential and immediate next steps"
    },
    "api_usage_analysis": {
      "type": "object",
      "properties": {
        "endpoints_used": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "List of API endpoints the lead has been using"
        },
        "usage_insights": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Insights about what their API usage patterns reveal about their needs and use case"
        },
        "potential_use_case": {
          "type": "string",
          "description": "Most likely use case based on API usage patterns"
        }
      },
      "required": ["endpoints_used", "usage_insights", "potential_use_case"]
    },
    "company_profile": {
      "type": "object",
      "properties": {
        "company_overview": {
          "type": "string",
          "description": "Brief overview of the company including industry, size, and business model"
        },
        "fit_assessment": {
          "type": "string",
          "description": "Why this company is (or isn't) a good fit for Explorium based on their profile"
        },
        "potential_value": {
          "type": "string",
          "description": "Estimated potential value or deal size based on company size and use case"
        }
      },
      "required": ["company_overview", "fit_assessment", "potential_value"]
    },
    "quality_signals": {
      "type": "object",
      "properties": {
        "positive_signals": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Positive indicators of lead quality (legitimate company email, active usage, enterprise size, etc.)"
        },
        "concerns": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Any red flags or concerns (generic email, low usage, unclear use case, etc.). Leave empty if no concerns."
        }
      },
      "required": ["positive_signals", "concerns"]
    },
    "recommended_actions": {
      "type": "object",
      "properties": {
        "immediate_next_step": {
          "type": "string",
          "description": "The single most important action sales should take right now"
        },
        "outreach_timing": {
          "type": "string",
          "description": "When to reach out (immediately, within 24hrs, this week, monitor first, etc.)"
        },
        "suggested_approach": {
          "type": "string",
          "description": "How to approach this lead (email, call, demo offer, etc.) and tone to use"
        }
      },
      "required": ["immediate_next_step", "outreach_timing", "suggested_approach"]
    },
    "talking_points": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Specific, personalized talking points based on their API usage and company profile that sales can reference in conversation"
    }
  },
  "required": [
    "lead_score",
    "quick_summary",
    "api_usage_analysis",
    "company_profile",
    "quality_signals",
    "recommended_actions",
    "talking_points"
  ]
}
